define(['jquery', 'oae.core'], function($, oae) {

    return function(uid, showSettings, widgetData) {

        // The widget container
        var $rootel = $('#' + uid);

        // Keeps track of current context profile
        var contextProfile = null;

        // Keeps track of the id of the recording that needs to be deleted
        var recordingToDelete = null;

        /**
         * Delete a recording and send out an event to let other widgets know whether it succeeded.
         * The deleted recording's ID `recordingId` and the deleted recording object `softDeleted` will be included
         * in the `oae.deletecoment.done` event that's fired off when the recording was successfully deleted.
         */
        var deleteRecording = function() {
            oae.api.meeting.deleteRecording(recordingToDelete, function(err, info) {
                if (err) {
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__RECORDING_NOT_DELETED__', 'deleterecording'),
                        oae.api.i18n.translate('__MSG__RECORDING_DELETE_FAIL__', 'deleterecording'),
                        'error');
                } else {
                    // Fire off an event to let other widgets know that the recording was deleted
                    $(document).trigger('oae.deleterecording.done', {
                        'recordingId': recordingToDelete
                    });
                }

                // Hide the modal
                $('#deleterecording-modal', $rootel).modal('hide');
            });
        };

        /**
         * Initialize the delete recording widget
         */
        var initDeleteRecording = function() {
            $(document).on('click', '.oae-trigger-deleterecording', function(ev, data) {
                // Cache the ID of the recording that needs to be deleted
                recordingToDelete = $(ev.currentTarget).attr('data-id');

                // Receive the context's profile information and set up the delete recording modal
                $(document).on('oae.context.send.deleterecordings', function(ev, contextData) {
                    contextProfile = contextData;
                    // Show the delete recording confirmation modal
                    $('#deleterecording-modal', $rootel).modal({
                        'backdrop': 'static'
                    });
                });
                // Request the context profile information
                $(document).trigger('oae.context.get', 'deleterecordings');
            });

            // Bind the delete confirmation button
            $rootel.on('click', '#deleterecording-delete', deleteRecording);
        };

        initDeleteRecording();

    };
});
