define(['jquery', 'oae.core'], function($, oae) {

    return function(uid, showSettings, widgetData) {

    	// The widget container
        var $rootel = $('#' + uid);

        /**
         * Render the meeting description
         */
        var renderMeeting = function(id, description) {
            $('#meeting-description', $rootel).html(oae.api.util.security().encodeForHTMLWithLinks(description));

            //The status is show based on the meetingInfo and permissions for the user
            $('#meeting-status', $rootel).html('<div class="alert alert-info" role="alert">' + oae.api.i18n.translate('__MSG__ALERT_MEETING_READY__', 'meeting') + '</div>');

            //var html_button_join = '<button id="meeting-default-join" type="button" class="btn btn-primary oae-trigger-joinmeeting">' + oae.api.i18n.translate('__MSG__JOIN_MEETING__', 'meeting') + '</button>';
            var html_button_join = '<a href="/api/meeting/ui/' + id + '/join" id="meeting-default-join" class="btn" target="_blank"><i class="fa fa-external-link pull-left"></i>' + oae.api.i18n.translate('__MSG__JOIN_MEETING__', 'meeting') + '</a>';

            $('#meeting-actionbar', $rootel).html(html_button_join);
        };

        /**
         * Initialize the meeting description and add a binding for description updates
         */
        var initMeeting = function() {
            if (widgetData.description) {
                renderMeeting(widgetData.id, widgetData.description);
            }

            $(document).on('oae.editmeeting.done', function(ev, data) {
                renderMeeting(data.description);
            });
        };

        initMeeting();
    };
});
